import React from 'react'
import USChart from '../Charts/UnitedStatesCases'
import IndiaChart from '../Charts/IndiaCases'
import IndiaButton from './IndiaButton'
import USButton from './USButton'

class Home extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
          showIndia: false
        };

      // This binding is necessary to make `this` work in the callback
      this.handleClickIndia = this.handleClickIndia.bind(this);
      this.handleClickUS = this.handleClickUS.bind(this);
    }

    handleClickIndia() {
      this.setState(state => ({
        showIndia: true
      }));
    }

    handleClickUS() {
        this.setState(state => ({
          showIndia: false
        }));
      }

    render() {
        if(!this.state.showIndia)
        {
            return (
                <div>
                    <USChart />
                    <button type="button" class="btn btn-outline-primary btn-toolbar">
                    United States
                    </button>
                    <button type="button" class="btn btn-outline-primary btn-toolbar" onClick={this.handleClickIndia}>
                    India
                    </button>
                </div>
            );
        }

        return (
                <div>
                    <IndiaChart />
                        <button type="button" class="btn btn-outline-primary btn-toolbar" onClick={this.handleClickUS}>
                        United States
                        </button>
                        <button type="button" class="btn btn-outline-primary btn-toolbar">
                        India
                        </button>
                </div>
        )
    }
  }

export default Home;
