import React, { Component } from 'react';
import Chart from "chart.js";
import {Line} from 'react-chartjs-2';

class Chartjs extends Component {

	constructor() {
		super(props);

		this.state = {
			chartData:{
				//Bring in data
	              labels: this.props.data.dates,
	              datasets: [
	                  {
	                      label: "Daily Cases",
	                      data: this.props.data.new_daily_cases,
	                      backgroundColor: [
	                      'rgba(105, 0, 132, .2)',
	                      ],
	                      borderColor: [
	                      'rgba(200, 99, 132, .7)',
	                      ],
	                      borderWidth: 2
	                  },
	                  {
	                    label: "Daily Deaths",
	                    data: this.props.data.new_daily_deaths,
	                    backgroundColor: [
	                    'rgba(0, 137, 132, .2)',
	                    ],
	                    borderColor: [
	                    'rgba(0, 10, 130, .7)',
	                    ],
	                    borderWidth: 2
	                  }

	              ]
	          }
			}
		}


	render() {
		return (
			<div className='chartjs'>
				<Line
					data={this.state.chartData}
					options={ { responsive: true } }
				/>

			</div>
			);
	}
}

export default Chartjs;
