import React, { Component } from 'react';
import { wait } from '@testing-library/react';
import Plot from 'react-plotly.js';
import { LineChart, Line } from 'recharts';


class Company extends Component {

    constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  getUS_Timeline() {
    fetch('https://cors-anywhere.herokuapp.com/https://thevirustracker.com/free-api?countryTimeline=US')
      .then(res => res.json())
      .then(
        (result) => {
          // console.log(result);
          this.setState({
            isLoaded: true,
            items: result
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          console.log('Could not access data');
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  componentDidMount() {
    this.getUS_Timeline();
  }

  arrangeForGraph() {
    var data = this.state.items;
    var dates = [];
    var new_daily_cases = [];
    var new_daily_deaths = [];
    for(var key in data['timelineitems'][0]) {
      new_daily_deaths.push(data['timelineitems'][0][key]['new_daily_deaths']);
      new_daily_cases.push(data['timelineitems'][0][key]['new_daily_cases']);

      var date = new Date(key);
      dates.push(key);
    } 

    return {dates: dates, new_daily_cases: new_daily_cases, new_daily_deaths: new_daily_deaths};

  }

  render() {
    const { error, isLoaded, items } = this.state;
    if(isLoaded) {
      var object = this.arrangeForGraph(items);
      console.log(object);

      return (
                <div>
                    <LineChart width={400} height={400} data={object}>
                      <Line type="monotone" dataKey="dates" stroke="#8884d8" />
                    </LineChart>
                  );
                  </div>

          )
    }

    return (
        <p>Loading graph...</p>
      )
  }

}

export default Company;
