import React from 'react'
import USChart from '../Charts/UnitedStatesCases'
import IndiaChart from '../Charts/IndiaCases'
import IndiaButton from './IndiaButton'
import USButton from './USButton'

class Home extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
          showIndia: false
        };

      // This binding is necessary to make `this` work in the callback
      this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
      this.setState(state => ({
        showIndia: true
      }));
    }

    render() {
        if(!showIndia)
        {
            return (
                <div>
                    <button type="button" class="btn btn-outline-primary">
                    United States
                    </button>
                    <button type="button" class="btn btn-outline-primary" onClick={this.handleClick}>
                    India
                    </button>
                </div>
            );
        }
    }
  }

export default Home;
