import React, { Component } from 'react';
import { wait } from '@testing-library/react';
import Plot from 'react-plotly.js';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';

import Chart from "chart.js";

import Chartjs from './Chartjs'



class Company extends Component {

    constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  getUS_Timeline() {
    fetch('https://cors-anywhere.herokuapp.com/https://thevirustracker.com/free-api?countryTimeline=US')
      .then(res => res.json())
      .then(
        (result) => {
          // console.log(result);
          this.setState({
            isLoaded: true,
            items: result
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          console.log('Could not access data');
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  componentDidMount() {
    this.getUS_Timeline();
  }

  arrangeForGraph() {
    var data = this.state.items;
    var objectArray = [];
    var day = 1;

    for(var key in data['timelineitems'][0]) {
      var date = new Date(key);

      var obj = {
              new_daily_deaths:data['timelineitems'][0][key]['new_daily_deaths'],
              new_daily_cases:data['timelineitems'][0][key]['new_daily_cases'],
              dates:date,
              day: day
            }
      objectArray.push(obj);
      day += 1;
    } 

    objectArray.pop();

    return objectArray;

  }


  render() {
    const { error, isLoaded, items } = this.state;
    if(isLoaded) {
      var data = this.arrangeForGraph(items);
      console.log(data);   

      return (
                <div className = "chart">
                    <LineChart width={500} height={300} data={data}>
                      <XAxis dataKey="dates"/>
                      <YAxis dataKey=""/>
                      <CartesianGrid stroke="#efe" strokeDasharray="5 5"/>
                      <Line type="monotone" dataKey="new_daily_cases" stroke="#8884d8" />
                      <Line type="monotone" dataKey="new_daily_deaths" stroke="#82ca9d" />
                    </LineChart>

                    <Chartjs />

                  </div>

          );
    }

    return (
        <p>Loading graph...</p>
      )
  }

}

export default Company;
