import React, { Component } from 'react'
import USChart from '../Charts/UnitedStatesCases'
import IndiaChart from '../Charts/IndiaCases'
import IndiaButton from './IndiaButton'
import USButton from './USButton'
import UnitedStatesCases from '../Charts/UnitedStatesCases'

class Home extends Component() {

    constructor(props) {
        super(props);
        this.state = {
            india:false,
            usa:false
        }
    }

    showIndia() {
        return (
            <IndiaChart />
        )
    }

    showUS() {
        return (
            <UnitedStatesCases />
        )
    }

    render() {
        return (
            <div>
                <button type="button" class="btn btn-outline-primary" onClick={ this.state.india = true }>India</button>
                <button type="button" class="btn btn-outline-primary" onClick={ this.state.usa = true }>United States</button>
            </div>
        );
    }
}

export default Home;
